import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { HttpClientModule } from '@angular/common/http';
import { FormsModule } from '@angular/forms';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { RouterModule } from '@angular/router'; // <-- Importamos RouterModule

import { AppComponent } from './app.component';
import { FooterComponent } from './components/footer/footer.component';
import { HomeComponent } from './pages/home/home.component';  // <-- Importamos HomeComponent
import { AboutComponent } from './pages/about/about.component'; // <-- Importamos AboutComponent

@NgModule({
  declarations: [
    AppComponent,
    FooterComponent,
    HomeComponent,  // <-- Agregamos HomeComponent
    AboutComponent  // <-- Agregamos AboutComponent
  ],
  imports: [
    BrowserModule,
    HttpClientModule,
    FormsModule,
    BrowserAnimationsModule,
    MatProgressSpinnerModule,
    RouterModule.forRoot([]), // <-- Importante para que funcione el enrutamiento
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
