import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  mensaje: string = '';

  mostrarMensaje(): void {
    this.mensaje = '¡Bienvenido a nuestra plataforma! Esperamos que disfrutes de todas las funciones.';
  }
}
