export class HomeComponent {
  mensaje: string;

  constructor() {
    this.mensaje = ''; // Inicia con una cadena vacía
  }

  mostrarMensaje() {
    this.mensaje = '¡Bienvenido a nuestra plataforma! Estamos felices de tenerte aquí.';
  }
}
